    return (
        <div>
    {detail.map(detail => (
                    <div class="flex flex-col bg-gray-200 rounded-lg p-4 m-2">
                        <div class="flex flex-col items-start mt-4">
                            <a href="#" class="block max-w-sm p-6 bg-white border border-gray-200 rounded-lg shadow hover:bg-gray-100 dark:bg-gray-800 dark:border-gray-700 dark:hover:bg-gray-700">
                                <h5 class="mb-2 text-2xl font-bold tracking-tight text-gray-900 dark:text-white">Nama Hewan {detail.nama_hewan}</h5>
                                <p class="font-normal text-gray-700 dark:text-gray-400">Provinsi : {detail.nama_provinsi}</p>
                                <p class="font-normal text-gray-700 dark:text-gray-400">Alamat   : {detail.alamat_peternakan}</p>
                                <p class="font-normal text-gray-700 dark:text-gray-400">No. Telp : {detail.no_telp}</p>
                            </a>
                        </div>
                    </div>
                ))}
        </div>
    )
}
