import { useState, useEffect} from "react";
import axios from "axios";

export default function Search() {

    const [peternakan, setPeternakan] = useState([]);
    const [mounted, setMounted] = useState(false);
    const [isLoading, setIsLoading] = useState(true);
    const [namaUser, setNamaUser] = useState();
    const [search, setSearch] =  useState('');

    const getData = async search => {
        const token = localStorage.getItem('token');
        console.log(token);
        const tokenType = localStorage.getItem('token_type');
        const response = await fetch('http://localhost:8000/api/pet_pro/' + search, {
          method: 'GET',
          headers: {
            'Content-Type': 'application/json',
            'Accept': 'application/json',
            'Authorization': 'Bearer ' + token
          }
        })
        // .then(data => data.json())
        const data = await response.json()
        console.log(data.peternakan);
      }

    useEffect(() => {
        if (!mounted) {
            setMounted(true);
        }
        else {
            // getData();
            setNamaUser(localStorage.getItem('name'));
        }
    }, [mounted]);

    const submitHandler = e => {
        e.preventDefault();
        getData(search);
    }


  return (
    <section>
     
    <form onSubmit={submitHandler}>
    <input type="text" name="search" onChange={e => setSearch(e.target.value)} value={search}/>
    <button >
    Search
        
    </button>

    </form>


    </section>
  )
}
