import React, { useState } from "react";
import { useRouter } from "react";
import swal from 'sweetalert';


async function RegistUser(credentials) {
  return fetch('http://127.0.0.1:8000/api/register', {
    method: 'POST',
    dataType: "json",
    headers: {
      'Content-Type': 'application/json'
    },
    body: JSON.stringify(credentials)
  })
  .then(data => data.json())
}

export default function Regis () {
  
  const [name, setName] = useState();
  const [email, setEmail] = useState();
  const [password, setPassword] = useState();

  const handleSubmit = async e => {
    e.preventDefault();
    const response = await RegistUser({
      name,
      email,
      password
    });
    console.log(response);
    if ('token' in response.success) {
        swal("Success", response.success.message, "success", {
          buttons: true,
          timer: 2000,
        })
      .then((value) => {
        localStorage.setItem('token', response['token']);
        localStorage.setItem('user', JSON.stringify(response['user']));
        window.location.href = "/";
      });
    } else {
      swal("Failed", response.message, "error");
      }
      }

  
    return (
        <section>
        <div class="max-w-xl py-6 px-8 h-80 mt-20 bg-white justify-content-center rounded shadow-xl">
  <form action="" method="POST" onSubmit={handleSubmit} >
    <div class="mb-6">
      <label for="name" class="block text-gray-800 font-bold">Name:</label>
      <input 
      id="name" 
      type="text" 
      name="name" 
      onChange={e => setName(e.target.value)}
      placeholder="Your Name" 
      class="w-full border border-gray-300 py-2 pl-3 rounded mt-2 outline-none focus:ring-indigo-600 :ring-indigo-600" />
    </div>
    <div>
