import { useState, useEffect} from "react";
import axios from "axios";


function Peternakan() {

    const [peternakans, setPeternakan] = useState([]);
    const [mounted, setMounted] = useState(false);
    const [isLoading, setIsLoading] = useState(true);
    const [namaUser, setNamaUser] = useState();

    const getData = async () => {
        const token = localStorage.getItem('token');
        const tokenType = localStorage.getItem('token_type');
        const response = await fetch('http://localhost:8000/api/peternakan', {
            method: 'GET',
            headers: {
                'Content-Type': 'application/json',
                'Accept': 'application/json',
                'Authorization': 'Bearer' + ' ' + token
            }
        }).catch(err => console.log(err));
        const data = await response.json();
        console.log(data.data);
        setPeternakan(data.data);
        setTimeout(() => {
            setIsLoading(false);
        }, 1000);
        console.log(token);
    }

    useEffect(() => {
        if (!mounted) {
            setMounted(true);
        }
        else {
            getData();
            setNamaUser(sessionStorage.getItem('name'));
        }
    }, [mounted]);

  return (
      <div className="container-fluid card-container d-flex justify-content-center align-items-center gap-4 flex-wrap">
        {
          peternakans && peternakans.map(peternakan =>      
          <div className="card col-3 shadow p-3">
          <div>
            <strong>Alamat : </strong>{peternakan.alamat_peternakan}
          </div>
          <div>
            <strong>Nama Peternakan : </strong>{peternakan.nama_peternakan}
          </div>
          <div>
            <strong>No. Telpon : </strong>{peternakan.no_telp}
          </div>
        </div>)
        }
      </div>
  );
 
}
export default Peternakan;
